'From MIT Squeak 0.9.4 (June 1, 2003) [No updates present.] on 30 May 2020 at 9:51:06 am'!!MicrobitCode methodsFor: 'private' stamp: 'EiichiroIto 5/30/2020 09:47'!useUtime	modules add: MicrobitModule utime.! !!MicrobitCode methodsFor: 'sensor blocks' stamp: 'EiichiroIto 5/30/2020 09:47'!ticksMs: aMorph	self useUtime.	^ 'utime.ticks_ms()'! !!MicrobitCode methodsFor: 'sensor blocks'!ticksUs: aMorph	self useUtime.	^ 'utime.ticks_us()'! !!MicrobitModule class methodsFor: 'instance creation' stamp: 'EiichiroIto 5/30/2020 09:46'!utime	^ self new moduleName: #utime;	 yourself! !!MicrobitSpriteMorph methodsFor: 'sensing ops' stamp: 'EiichiroIto 5/30/2020 09:45'!ticksMs	^ self runningTime! !!MicrobitSpriteMorph methodsFor: 'sensing ops' stamp: 'EiichiroIto 5/30/2020 09:45'!ticksUs	^ self runningTime * 1000! !!MicrobitSpriteMorph class methodsFor: 'block specs'!sensingBlocks	^ #(		'sensing'			('running time'			r	runningTime)			('ticks ms'					r	ticksMs)			('ticks us'					r	ticksUs)			('light'							r	light)			('temperature'				r	temperature)			('%H sensor value'		r	sensorValueOf: 'accelX')			-			('current gesture'		r	currentGesture)			('is gesture %m ?'		b	isGesture: up)			('was gesture %m ?'		b	wasGesture: up)			('gestures'					r	gestures)			-			('calibrate compass'		-	calibrateCompass)			('is calibrated'				b	isCalibrated)			('heading'						r	headingCompass)			('clear calibration'		-	clearCalibration)			('field strength'			r	fieldStrength)			-			('usonic distance'		r	usonicDistance)	)! !MicrobitSpriteMorph removeSelector: #runningTimeUs!