'From MIT Squeak 0.9.4 (June 1, 2003) [No updates present.] on 26 December 2022 at 5:28:18 pm'!!MicrobitCode methodsFor: 'operators blocks' stamp: 'EiichiroIto 12/26/2022 17:24'!asciiOf: aMorph 	| tmp |	tmp := self unaryFunction: 'str' of: aMorph.	^ 'bytes([ord(_) for _ in ' , tmp , '])'! !!ScriptableScratchMorph methodsFor: 'string ops'!asciiOf: aString 	^ aString asString asArray collect: [:each | each asciiValue]! !!ScriptableScratchMorph class methodsFor: 'microwitch'!operatorsBlocks	^ #(		'operators'			('%n + %n'						r	x:plusY: - -)			('%n - %n'						r	x:minusY: - -)			('%n * %n'						r	x:mulY: - -)			('%n / %n'						r	x:divY: - -)			('%n mod %n'					r	x:modY: - -)			-			('pick random %n to %n'		r	randomFrom:to: 1 10)			('set random seed to %n'		-	setRandomSeed: 1234)			-			('%s < %s'						b	less:than: '' '')			('%s = %s'						b	equal:to: '' '')			('%s > %s'						b	more:than: '' '')			('%n between %n and %n'												b	is:between:and:	5 1 10)			('%s is None'				b	isNone:)			-			('%b and %b'				b	x:andY:)			('%b or %b'					b	x:orY:)			('not %b'						b	not:)			('true'							b	true)			('false'							b	false)			-			('%n bitand %n'			r	x:bitandY:	2	6)			('%n bitor %n'				r	x:bitorY:		2	6)			('%n bitxor %n'			r	x:bitxorY:	2	6)			('%n bit shift left %n'												r	x:bitShiftLeft:	1	5)			('%n bit shift right %n'												r	x:bitShiftRight:	256	6)			-			('string of %n'				r	stringOf:	0)			('number of %s'			r	numberOf: '123')			('join %s %s'				r	concatenate:with: 'hello ' 'world')			('letter %n of %s'			r	letter:of: 0 'world')			('length of %s'				r	stringLength: 'world')			('any of %s'					r	anyOf: )			('ascii of %s'				r	asciiOf:	'ABC')			-			('round %n'					r	rounded -)			('abs %n'						r	abs -)			('abs %n sign %n'			r	abs:sign:	- -)			-			('%f of %n'					r	computeFunction:of: 'sqrt' 10)	)! !