'From MIT Squeak 0.9.4 (June 1, 2003) [No updates present.] on 28 December 2022 at 9:57:52 am'!!LedArgMorph methodsFor: 'private'!showLedEditorAt: aPoint 	| morph ret pos |	World submorphs do: [:m | (m isKindOf: MicrobitLedEditorMorph)			ifTrue: [m delete]].	morph := MicrobitLedEditorMorph new.	morph image: self image.	pos := (World extent - morph extent) min: aPoint.	morph position: pos.	ret := morph getUserResponse.	ret ifFalse: [^ self].	self image: morph image! !