	5	100	500)			('servo set pin %j %n msec'					-		setServo:msec:					0	5)			-			('I2C read addr %n'					r	i2cRead:	0)			('I2C write addr %n data %L'	-	i2cWrite:list:	0)		'neoPixel'			('set %n to r %n g %n b %n of NeoPixel pin %k'		-		setNo:r:g:b:neoPixel: 0 255 255 255 '')			('show NeoPixel of pin %k'								-		showNeoPixel:)			('clear NeoPixel of pin %k'								-		clearNeoPixel:)		)! !